	    , createUserId  --作成者ID
	    , updateDate    --更新日時
	    , updateUserId  --更新者ID
	    , versionNo     --バージョンNo
    ) values (
	      #{session_id}
		  , #{user_id}
	    , CURRENT_TIMESTAMP
	    , #{createUserId}
	    , CURRENT_TIMESTAMP
	    , #{createUserId}
	    , 1
    )
