mapper.update:
update t_ticket_progress set
  available = 'no'
where project_id = #{project_id}

mapper.insert:
insert into t_ticket_progress (
    project_id  --プロジェクトID
  , id  --識別子
  , disp_seq  --表示順
  , name  --進捗名称
  , available  --有効フラグ
  , createUserId  --作成者ID
  , updateUserId  --更新者ID
) values (
    #{project_id}
  , #{id}
  , #{disp_seq}
  , #{name}
  , 'yes'
  , #{createUserId}
  , #{createUserId}
)

mapper.update:
update t_ticket_progress set
    disp_seq = #{disp_seq}
  , name = #{name}
  , available = 'yes'
  , updateDate = CURRENT_TIMESTAMP
  , updateUserId = #{updateUserId}
  , versionNo = versionNo + 1
where project_id = #{project_id}
  and id = #{id}
