mapper.insert:
insert into m_user (
    id       --識別子
  , login_id --ログインID
  , passwd   --パスワード
  , name1    --性
  , name2    --名
  , email    --メールアドレス
  , language_id  --言語ID
  , createUserId  --作成者ID
  , updateUserId  --更新者ID
) values (

    coalesce((select max(id) from m_user), 0) + 1


    #{id}

  , #{login_id}
  , #{passwd}
  , #{name1}
  , #{name2}
  , #{email}
  , #{language_id}
  , #{createUserId}
  , #{createUserId}
)
